public static double MedianOfFive(double a, double b, double c, double d, double e)\n{\n    // Step 1: Order each pair (2 comparisons)\n    double minAB = Math.Min(a, b);\n    double maxAB = Math.Max(a, b);\n    double minCD = Math.Min(c, d);\n    double maxCD = Math.Max(c, d);\n\n    // Step 2: Eliminate the lowest one from the possibilities (1 comparison)\n    double minABCD = Math.Min(minAB, minCD);\n\n    // Step 3: Add in the 5th number and compare (1 comparison)\n    double minABCDE = Math.Min(minABCD, e);\n\n    // Step 4: Compare the two lowest of the two new pairs and eliminate the lower one (1 comparison)\n    double median = Math.Min(maxAB, maxCD);\n    median = Math.Min(median, minABCDE);\n\n    // Step 5: Return the median\n    return median;\n}