private void STAProcessSelection(object argument)\n{\n    ISelection selection = argument as ISelection;\n    if (selection != null)\n    {\n        processingResult = controller.ProcessSelection(selection);\n        // Increment the reference count of the COM objects in controller.List\n        foreach (object obj in controller.List)\n        {\n            Marshal.AddRef(Marshal.GetIUnknownForObject(obj));\n        }\n    }\n}