using System;\nusing System.IO;\nusing Ghostscript.NET;\n\npublic class PdfConverter\n{\n    public void ConvertPdfToJpeg(string inputPdfPath, string outputJpegPath)\n    {\n        using (var rasterizer = new GhostscriptRasterizer())\n        {\n            rasterizer.Open(inputPdfPath);\n            var img = rasterizer.GetPage(1, 96, 96);\n            img.Save(outputJpegPath, System.Drawing.Imaging.ImageFormat.Jpeg);\n        }\n    }\n\n    public void ConvertPdfToPng(string inputPdfPath, string outputPngPath)\n    {\n        using (var rasterizer = new GhostscriptRasterizer())\n        {\n            rasterizer.Open(inputPdfPath);\n            var img = rasterizer.GetPage(1, 96, 96);\n            img.Save(outputPngPath, System.Drawing.Imaging.ImageFormat.Png);\n        }\n    }\n}