protected void Application_Error(object sender, EventArgs e)\n{\n    var exception = Server.GetLastError();\n    var statusCode = exception is HttpException httpException ? httpException.GetHttpCode() : 500;\n\n    if (statusCode == 404)\n    {\n        // Log the error and redirect to the new URL\n        Response.StatusCode = 301;\n        Response.StatusDescription = "Moved Permanently";\n        Response.RedirectPermanent("/new-url", true);\n    }\n    else\n    {\n        // Handle other errors\n    }\n}