// Create a new parameter\nSqlParameter word1Parameter = new SqlParameter("@word1", SqlDbType.NVarChar, 50);\nword1Parameter.SourceColumn = null; // not bound to a specific column\n\nSqlParameter word2Parameter = new SqlParameter("@word2", SqlDbType.NVarChar, 50);\nword2Parameter.SourceColumn = null; // not bound to a specific column\n\n// Add the parameters to the table adapter\nthis.Adapter.SelectCommand.Parameters.Add(word1Parameter);\nthis.Adapter.SelectCommand.Parameters.Add(word2Parameter);\n\n// Modify your query to use the parameters\nthis.Adapter.SelectCommand.CommandText = "SELECT  * " +\n                                         "FROM    Orders " +\n                                         "WHERE   (id_order = @id_order) OR " +\n                                         "(IsFor LIKE '%' + @word1 + '%') OR " +\n                                         "(IsFor LIKE '%' + @word2 + '%') OR " +\n                                         "(IsFrom LIKE '%' + @word1 + '%') OR " +\n                                         "(IsFrom LIKE '%' + @word2 + '%') ";