using System.Security.Cryptography;\n\npublic class RSAKeyGenerator\n{\n    public static (string publicKey, string privateKey) GenerateKeyPair()\n    {\n        using var rsa = new RSACryptoServiceProvider(2048);\n        rsa.FromXmlString("<RSAKeyValue></RSAKeyValue>"); // Initialize with empty key\n\n        // Generate a new key pair\n        rsa.KeySize = 2048;\n        rsa.GenerateKey();\n\n        // Export the public and private keys as XML strings\n        var publicKey = rsa.ToXmlString(false); // False for public key only\n        var privateKey = rsa.ToXmlString(true); // True for private key\n\n        return (publicKey, privateKey);\n    }\n}