using Microsoft.VisualStudio.TestTools.UnitTesting;\nusing Microsoft.VisualStudio.TestTools.UnitTesting.Web;\n\nnamespace MyWebTestProject\n{\n    [TestClass]\n    public class MyWebTest\n    {\n        [TestMethod]\n        public void TestMyWebApp()\n        {\n            // Create a new web test request\n            WebTestRequest request = new WebTestRequest("http://mywebapp.com");\n\n            // Add a web test step to navigate to the home page\n            WebTestStep step1 = new WebTestStep("Navigate to Home Page");\n            step1.NavigateToUrl(new Uri("http://mywebapp.com"));\n            request.Steps.Add(step1);\n\n            // Add a web test step to click on a button\n            WebTestStep step2 = new WebTestStep("Click on Button");\n            step2.ClickElement(new HtmlElement("button"));\n            request.Steps.Add(step2);\n\n            // Run the web test\n            WebTestResponse response = request.GetResponse();\n\n            // Verify the web test response\n            Assert.AreEqual(HttpStatusCode.OK, response.StatusCode);\n        }\n    }\n}