using System;\nusing System.IO;\nusing System.Data.OleDb;\n\npublic class ExcelReader\n{\n    public void ReadExcelFile(byte[] fileBytes)\n    {\n        // Create a temporary file\n        string tempFile = Path.GetTempFileName();\n        File.WriteAllBytes(tempFile, fileBytes);\n\n        // Create an OleDbConnection to the temporary file\n        string connectionString = $"Provider=Microsoft.ACE.OLEDB.12.0;Data Source={tempFile};Extended Properties='Excel 12.0;HDR=YES;'";\n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            connection.Open();\n\n            // Read the Excel file\n            OleDbCommand command = new OleDbCommand("SELECT * FROM [Sheet1$]", connection);\n            OleDbDataReader reader = command.ExecuteReader();\n\n            // Process the data\n            while (reader.Read())\n            {\n                Console.WriteLine(reader[0].ToString());\n            }\n        }\n\n        // Delete the temporary file\n        File.Delete(tempFile);\n    }\n}