using System;\nusing System.IdentityModel.Selectors;\nusing System.Security.Cryptography.X509Certificates;\n\nnamespace MyClient\n{\n    public class CustomX509CertificateValidator : X509CertificateValidator\n    {\n        public override void Validate(X509Certificate2 certificate)\n        {\n            // Implement your custom certificate validation logic here\n            // For example, you can check the certificate's subject name or thumbprint\n            if (certificate.SubjectName.Name != "CN=srs204.mywebsite.ca")\n            {\n                throw new SecurityException("Invalid certificate subject name");\n            }\n        }\n    }\n}