public static class MyExtensions\n{\n    public static void MyExtensionMethod(this MyObject obj)\n    {\n        Console.WriteLine("Inside extension method");\n        Console.WriteLine(obj.MyProperty); // This will throw a null reference exception\n    }\n}\n\npublic class MyObject\n{\n    public string MyProperty { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        MyObject myObject = null;\n        myObject.MyExtensionMethod();\n    }\n}