var query = db.ForumStatsSet\n    .Where(s => s.LogDate >= date1 && s.LogDate <= date2)\n    .Join(db.Topics, s => s.TopicId, t => t.TopicId, (s, t) => new { s, t })\n    .Join(db.Forums, x => x.t.ForumId, f => f.ForumId, (x, f) => new { x.s, x.t, f })\n    .Join(db.ForumGroups, x => x.f.ForumGroupId, g => g.ForumGroupId, (x, g) => new { x.s, x.t, x.f, g })\n    .GroupBy(x => new { x.t.TopicId, x.t.Subject, x.f.ForumName, g.Name })\n    .OrderByDescending(g => g.Count())\n    .Select(g => new TopicStatsData\n    {\n        TopicId = g.Key.TopicId,\n        Count = g.Count(),\n        Subject = g.Key.Subject,\n        ForumGroupName = g.Key.Name,\n        ForumName = g.Key.ForumName,\n        ForumId = g.Key.ForumId\n    });