using System;\nusing System.Net;\n\nclass FtpClient\n{\n    private string ftpServer;\n    private string ftpUsername;\n    private string ftpPassword;\n\n    public FtpClient(string ftpServer, string ftpUsername, string ftpPassword)\n    {\n        this.ftpServer = ftpServer;\n        this.ftpUsername = ftpUsername;\n        this.ftpPassword = ftpPassword;\n    }\n\n    public void CreateFolder(string folderName)\n    {\n        FtpWebRequest request = (FtpWebRequest)WebRequest.Create($"ftp://{ftpServer}/{folderName}");\n        request.Method = WebRequestMethods.Ftp.MakeDirectory;\n        request.Credentials = new NetworkCredential(ftpUsername, ftpPassword);\n\n        try\n        {\n            FtpWebResponse response = (FtpWebResponse)request.GetResponse();\n            Console.WriteLine($"Folder '{folderName}' created successfully.");\n        }\n        catch (WebException ex)\n        {\n            if (ex.Response != null)\n            {\n                FtpWebResponse response = (FtpWebResponse)ex.Response;\n                if (response.StatusCode == FtpStatusCode.ActionNotTakenFileUnavailable)\n                {\n                    Console.WriteLine($"Folder '{folderName}' already exists.");\n                }\n                else\n                {\n                    Console.WriteLine($"Error creating folder '{folderName}': {ex.Message}");\n                }\n            }\n            else\n            {\n                Console.WriteLine($"Error creating folder '{folderName}': {ex.Message}");\n            }\n        }\n    }\n}