using System;\nusing System.Management;\n\nclass UpsShutdown\n{\n    static void Main(string[] args)\n    {\n        // Create a new ManagementObjectSearcher to query the UPS\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher("SELECT * FROM Win32_UPS");\n\n        // Get the first UPS device (assuming there's only one)\n        ManagementObject ups = searcher.Get()[0];\n\n        // Call the Shutdown method on the UPS device\n        ups.InvokeMethod("Shutdown", null);\n    }\n}