public class RotatedLabel : Label\n{\n    public int Angle { get; set; }\n\n    protected override void OnPaint(PaintEventArgs e)\n    {\n        var graphics = e.Graphics;\n        var stringFormat = new StringFormat();\n\n        graphics.TranslateTransform(0, this.ClientSize.Height);\n        graphics.RotateTransform(-Angle);\n\n        var rect = new Rectangle(0, 0, this.ClientSize.Height, this.ClientSize.Width);\n        graphics.DrawString(this.Text, this.Font, Brushes.Black, rect, stringFormat);\n\n        graphics.ResetTransform();\n    }\n}