using System;\nusing System.Diagnostics;\nusing System.IO;\n\nclass PclToPdfConverter\n{\n    public static void ConvertPclToPdf(string pclFilePath, string pdfFilePath)\n    {\n        string pcl6Path = @"C:\Path\To\pcl6.exe"; // adjust the path to the pcl6 executable\n        string arguments = $"-dPDFSETTINGS=/screen -sOutputFile={pdfFilePath} {pclFilePath}";\n\n        using (Process process = new Process())\n        {\n            process.StartInfo.FileName = pcl6Path;\n            process.StartInfo.Arguments = arguments;\n            process.StartInfo.UseShellExecute = false;\n            process.StartInfo.CreateNoWindow = true;\n            process.Start();\n\n            process.WaitForExit();\n        }\n    }\n}