using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class AnagramGrouper\n{\n    public static void GroupAnagrams(string[] words)\n    {\n        var anagramGroups = new Dictionary<string, List<string>>();\n\n        foreach (var word in words)\n        {\n            // Sort the characters in the word to create a key\n            var sortedWord = string.Concat(word.OrderBy(c => c));\n\n            if (anagramGroups.ContainsKey(sortedWord))\n            {\n                anagramGroups[sortedWord].Add(word);\n            }\n            else\n            {\n                anagramGroups[sortedWord] = new List<string> { word };\n            }\n        }\n\n        // Print each anagram group\n        foreach (var group in anagramGroups.Values)\n        {\n            Console.WriteLine(string.Join(" ", group));\n        }\n    }\n}