using System;\nusing System.IO.MemoryMappedFiles;\n\n// Create a memory-mapped file\nusing (var mmf = MemoryMappedFile.CreateNew("MySharedMemory", 1024))\n{\n    // Get a view accessor to the shared memory\n    using (var accessor = mmf.CreateViewAccessor())\n    {\n        // Write data to the shared memory\n        accessor.Write(0, "Hello, world!");\n    }\n}\n\n// In the other process...\nusing (var mmf = MemoryMappedFile.OpenExisting("MySharedMemory"))\n{\n    // Get a view accessor to the shared memory\n    using (var accessor = mmf.CreateViewAccessor())\n    {\n        // Read data from the shared memory\n        var data = accessor.ReadString(0, 1024);\n        Console.WriteLine(data); // Output: "Hello, world!"\n    }\n}