<#@ template language="C#" #>\n<#@ assembly name="System.Data" #>\n<#@ import namespace="System.Data.SqlClient" #>\n<#@ import namespace="System.Data" #>\n\n// Settings\nstring connectionString = "Your Connection String";\nstring databaseName = "Your Database Name";\n\n// Get the database schema\nSqlConnection connection = new SqlConnection(connectionString);\nconnection.Open();\nDataTable schema = connection.GetSchema("Tables");\n\n// Generate entities and mappers\nforeach (DataRow row in schema.Rows)\n{\n    string tableName = row["TABLE_NAME"].ToString();\n    string entityName = tableName.Substring(0, 1).ToUpper() + tableName.Substring(1);\n\n    // Generate entity\n    WriteLine("// Entity: " + entityName);\n    WriteLine("public class " + entityName);\n    WriteLine("{");\n    foreach (DataColumn column in schema.Columns)\n    {\n        WriteLine("    public " + column.DataType.Name + " " + column.ColumnName + " { get; set; }");\n    }\n    WriteLine("}");\n\n    // Generate mapper\n    WriteLine("// Mapper: " + entityName + "Mapper");\n    WriteLine("public static class " + entityName + "Mapper");\n    WriteLine("{");\n    WriteLine("    public static " + entityName + " Get" + entityName + "(int id)");\n    WriteLine("    {");\n    WriteLine("        " + entityName + " entity = new " + entityName + "();");\n    WriteLine("        using (SqlConnection connection = new SqlConnection(connectionString))");\n    WriteLine("        {");\n    WriteLine("            connection.Open();");\n    WriteLine("            SqlCommand command = new SqlCommand(\"Your Stored Procedure Name\", connection);");\n    WriteLine("            command.CommandType = CommandType.StoredProcedure;");\n    WriteLine("            command.Parameters.AddWithValue(\"@id\", id);");\n    WriteLine("            SqlDataReader reader = command.ExecuteReader();");\n    WriteLine("            while (reader.Read())");\n    WriteLine("            {");\n    WriteLine("                entity." + column.ColumnName + " = reader[\"" + column.ColumnName + "\"].ToString();");\n    WriteLine("            }");\n    WriteLine("        }");\n    WriteLine("        return entity;");\n    WriteLine("    }");\n    WriteLine("}");\n}