public abstract class ShoppingCartItem\n{\n    public abstract decimal Price { get; }\n    public abstract decimal Weight { get; }\n}\n\npublic class CartItem : ShoppingCartItem\n{\n    public decimal Price { get; set; }\n    public decimal Weight { get; set; }\n    // other properties specific to CartItem\n}\n\npublic class DescriptionText : ShoppingCartItem\n{\n    public string Text { get; set; }\n    public decimal Price => 0;\n    public decimal Weight => 0;\n}\n\npublic class ShoppingCart\n{\n    public List<ShoppingCartItem> Items { get; set; }\n\n    public decimal CalculateTotalPrice()\n    {\n        return Items.Sum(i => i.Price);\n    }\n\n    public decimal CalculateTotalWeight()\n    {\n        return Items.Sum(i => i.Weight);\n    }\n}