using System;\nusing System.Windows.Forms;\n\npublic partial class Form1 : Form\n{\n    public Form1()\n    {\n        InitializeComponent();\n\n        // Load the XML document and CSS file\n        string xmlFile = "path/to/your/xml/file.xml";\n        string cssFile = "path/to/your/css/file.css";\n\n        // Create a new WebBrowser control\n        WebBrowser webBrowser = new WebBrowser();\n\n        // Set the XML document as the web browser's document\n        webBrowser.DocumentText = File.ReadAllText(xmlFile);\n\n        // Add a style sheet to the web browser\n        webBrowser.Document.OpenNew(true);\n        webBrowser.Document.Write("<style>" + File.ReadAllText(cssFile) + "</style>");\n\n        // Display the web browser\n        this.Controls.Add(webBrowser);\n    }\n}