using iTextSharp.text.pdf;\n\npublic partial class Form1 : Form\n{\n    public Form1()\n    {\n        InitializeComponent();\n    }\n\n    private void button1_Click(object sender, EventArgs e)\n    {\n        using (PdfReader pdfReader = new PdfReader("path/to/document.pdf"))\n        {\n            richTextBox1.Text = ExtractTextFromPdf(pdfReader);\n        }\n    }\n\n    private string ExtractTextFromPdf(PdfReader pdfReader)\n    {\n        var text = new StringBuilder();\n        for (int page = 1; page <= pdfReader.NumberOfPages; page++)\n        {\n            var pageText = PdfTextExtractor.GetTextFromPage(pdfReader, page);\n            text.Append(pageText);\n        }\n        return text.ToString();\n    }\n}