protected void gvUsers_PageIndexChanging(object sender, GridViewPageEventArgs e)\n{\n    gvUsers.PageIndex = e.NewPageIndex;\n    BindData(); // Call your data binding method here\n}\n\nprivate void BindData()\n{\n    int pageSize = gvUsers.PageSize;\n    int pageIndex = gvUsers.PageIndex;\n    int startRow = pageIndex * pageSize;\n    int endRow = startRow + pageSize;\n\n    // Use your data access layer to fetch only the records for the current page\n    var users = GetUserList(startRow, endRow); // Implement this method to fetch data from your database\n\n    gvUsers.DataSource = users;\n    gvUsers.DataBind();\n}