using System;\nusing System.Runtime.InteropServices;\n\nclass ComBridgeExample\n{\n    [DllImport("ComBridge")]\n    static extern IntPtr CreateComBridge();\n\n    [DllImport("ComBridge")]\n    static extern void ReleaseComBridge(IntPtr bridge);\n\n    static void Main(string[] args)\n    {\n        // Create a COM bridge\n        IntPtr bridge = CreateComBridge();\n\n        // Create an instance of the 32-bit COM object\n        object comObject = Activator.CreateInstance(Type.GetTypeFromProgID("MyComObject"));\n\n        // Use the COM object\n        // ...\n\n        // Release the COM bridge\n        ReleaseComBridge(bridge);\n    }\n}