using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Xml.Linq;\n\npublic class ItemFrequency\n{\n    public string Att1 { get; set; }\n    public string Att2 { get; set; }\n    public int Frequency { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Load the XML file\n        XDocument doc = XDocument.Load("yourfile.xml");\n\n        // Group the items by Att1 and count the frequency\n        var itemFrequencies = doc.Descendants("item")\n            .GroupBy(x => x.Attribute("att1").Value)\n            .Select(g => new ItemFrequency\n            {\n                Att1 = g.Key,\n                Att2 = g.First().Attribute("att2").Value,\n                Frequency = g.Count()\n            })\n            .OrderByDescending(x => x.Frequency)\n            .Take(4);\n\n        // Print the top 4 items\n        foreach (var item in itemFrequencies)\n        {\n            Console.WriteLine($"Att1: {item.Att1}, Att2: {item.Att2}, Frequency: {item.Frequency}");\n        }\n    }\n}