using System;\nusing System.Windows;\n\nnamespace WpfApp1\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n\n            // Load the XML document and CSS file\n            string xmlFile = "path/to/your/xml/file.xml";\n            string cssFile = "path/to/your/css/file.css";\n\n            // Set the XML document as the web browser's document\n            webBrowser.NavigateToString(File.ReadAllText(xmlFile));\n\n            // Add a style sheet to the web browser\n            webBrowser.InvokeScript("execScript", new string[] { "document.write('<style>" + File.ReadAllText(cssFile) + "</style>')" });\n        }\n    }\n}