using System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    private void MainForm_Load(object sender, EventArgs e)\n    {\n        // Create a panel for the navigation menu\n        var navPanel = new Panel\n        {\n            Dock = DockStyle.Left,\n            Width = 200\n        };\n\n        // Create a menu strip for navigation\n        var menuStrip = new MenuStrip();\n        navPanel.Controls.Add(menuStrip);\n\n        // Add menu items for each form\n        foreach (var formType in GetFormTypes())\n        {\n            var menuItem = new ToolStripMenuItem(formType.Name);\n            menuItem.Click += (o, args) => ShowForm(formType);\n            menuStrip.Items.Add(menuItem);\n        }\n\n        // Create a docking panel for the forms\n        var formPanel = new Panel\n        {\n            Dock = DockStyle.Fill\n        };\n\n        // Add the panels to the main form\n        this.Controls.Add(navPanel);\n        this.Controls.Add(formPanel);\n    }\n\n    private Type[] GetFormTypes()\n    {\n        // Return an array of form types (e.g. ClientInfoForm, MedicationForm, etc.)\n        // You can use reflection or a config file to populate this array\n    }\n\n    private void ShowForm(Type formType)\n    {\n        // Create an instance of the form\n        var form = (Form)Activator.CreateInstance(formType);\n\n        // Show the form in the docking panel\n        form.Dock = DockStyle.Fill;\n        formPanel.Controls.Add(form);\n        form.Show();\n    }\n}