using Oracle.DataAccess.Client;\nusing System;\nusing System.Data;\n\nclass OracleTimeStampExample\n{\n    public static void RetrieveTimeStamp()\n    {\n        string connectionString = "Data Source=myOracleDB;User Id=myUser;Password=myPassword;";\n        string query = "SELECT my_timestamp_column FROM my_table";\n\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n            using (OracleCommand command = new OracleCommand(query, connection))\n            {\n                using (OracleDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        DateTime timeStamp = reader.GetDateTime(0);\n                        Console.WriteLine(timeStamp.ToString("yyyy-MM-dd HH:mm:ss.fff"));\n                    }\n                }\n            }\n        }\n    }\n}