// View.aspx\n<% using (Ajax.BeginForm("Action", "Controller", \n           new AjaxOptions { UpdateTargetId = "MyControl",  }))\n{ %>\n    <span id="MyControl">\n       <% Html.RenderPartial("MyControl"); %>\n    </span>\n    <input type="hidden" id="submitValue" name="submitValue" />\n<% } %>\n\n// MyControl.ascx\n<%@ Control Language="C#" Inherits="System.Web.Mvc.ViewUserControl" %>\n<input name="prev" type="submit" value="prev" onclick="$('#submitValue').val('prev');" />\n<input name="next" type="submit" value="next" onclick="$('#submitValue').val('next');" />