@using (Ajax.BeginForm("YourAction", "YourController", new AjaxOptions { \n    UpdateTargetId = "tabs-container", \n    OnComplete = "initTabs" \n}))\n{\n    // your form fields here\n}\n\n<script>\n    function initTabs() {\n        $("#tabs").tabs({\n            load: function (event, ui) {\n                // load partial view for each tab via AJAX\n                $.ajax({\n                    url: '@Url.Action("Tab1Action", "YourController")',\n                    type: 'GET',\n                    success: function (data) {\n                        $("#tab-1").html(data);\n                    }\n                });\n                // repeat for each tab\n            }\n        });\n    }\n</script>