// View\n@model MyModel\n\n<div>\n    @Html.DropDownListFor(m => m.CountryId, Model.Countries)\n    @Html.DropDownListFor(m => m.CityId, new SelectList(string.Empty, "Value", "Text"))\n</div>\n\n<script type="text/javascript">\n    $(document).ready(function () {\n        $("#CountryId").change(function () {\n            var countryId = $(this).val();\n            $.getJSON('@Url.Action("GetCities", "MyController")', { countryId: countryId }, function (data) {\n                $("#CityId").empty();\n                $.each(data, function (index, value) {\n                    $("#CityId").append("<option value='" + value.Value + "'>" + value.Text + "</option>");\n                });\n            });\n        });\n    });\n</script>