// Add a hidden field to store the selected value\n<asp:HiddenField ID="cityDropDownListValue" runat="server" />\n\n// Modify the GetProjectNameOnSuccess function to update the hidden field\nfunction GetProjectNameOnSuccess(data) {\n    // ... existing code ...\n    var selectedValue = data.d; // assuming data.d contains the selected value\n    $(container + 'cityDropDownListValue').val(selectedValue);\n}\n\n// Modify the cityDropDownList_DataBound event handler to set the selected value\nprotected void cityDropDownList_DataBound(object sender, EventArgs e)\n{\n    cityDropDownListValue.Value = cityDropDownList.SelectedValue;\n}