using System.ComponentModel;\nusing System.Configuration.Install;\n\nnamespace DeviceServer\n{\n    [RunInstaller(true)]\n    public class ProjectInstaller : Installer\n    {\n        public ProjectInstaller()\n        {\n            var serviceProcessInstaller = new ServiceProcessInstaller();\n            var serviceInstaller = new ServiceInstaller();\n\n            // Set the service details\n            serviceInstaller.ServiceName = "DeviceServer";\n            serviceInstaller.DisplayName = "Device Server";\n            serviceInstaller.Description = "A server that processes commands to a device over a serial port";\n\n            // Add the installers to the collection\n            Installers.Add(serviceProcessInstaller);\n            Installers.Add(serviceInstaller);\n        }\n    }\n}