// example.cs\nusing Sprache;\n\npublic class MyDslGrammar {\n    public static Parser<MyDslAst> MyDslParser {\n        get {\n            // define your grammar rules here using Sprache's combinators\n            return from x in Parse.Identifier\n                   from y in Parse.String("->")\n                   from z in Parse.Identifier\n                   select new MyDslAst(x, z);\n        }\n    }\n}