using DbUp;\nusing DbUp.Engine;\n\n// Create a new DbUp engine\nvar engine = new DbUpEngine();\n\n// Define the connection string and schema\nvar connectionString = "Server=myServer;Database=myDatabase;User Id=myUser;Password=myPassword;";\nvar schema = "dbo";\n\n// Define the scripts to run\nvar scripts = new[]\n{\n    "001_CreateTable.sql",\n    "002_AddColumn.sql",\n    "003_AlterTable.sql"\n};\n\n// Run the scripts\nengine.RunScripts(connectionString, schema, scripts);