string[] itemIDs = new string[] { "1", "2", "3" }; // replace with your actual item IDs\n\nstring sql = "SELECT [ItemID], [Name], [RelDate], [Price], [Status] FROM [item_k] WHERE [ItemID] IN (@itemIDs)";\n\nSqlCommand cmd = new SqlCommand(sql);\ncmd.Parameters.AddWithValue("@itemIDs", string.Join(",", itemIDs));\n\nGridView1.DataSource = cmd.ExecuteReader();\nGridView1.DataBind();