using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing HtmlAgilityPack;\n\nclass HttpProxy\n{\n    public async Task<string> GetModifiedHtmlAsync(string url)\n    {\n        // Send a GET request to the original URL\n        var response = await new HttpClient().GetAsync(url);\n\n        // Check if the response is HTML\n        if (response.IsSuccessStatusCode && response.Content.Headers.ContentType.MediaType == "text/html")\n        {\n            // Read the HTML content\n            var html = await response.Content.ReadAsStringAsync();\n\n            // Parse the HTML using HtmlAgilityPack\n            var doc = new HtmlDocument();\n            doc.LoadHtml(html);\n\n            // Modify the DOM as needed\n            // For example, remove all script tags\n            foreach (var script in doc.DocumentNode.SelectNodes("//script"))\n            {\n                script.Remove();\n            }\n\n            // Return the modified HTML\n            return doc.DocumentNode.OuterHtml;\n        }\n        else\n        {\n            return null;\n        }\n    }\n}\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        var proxy = new HttpProxy();\n        var url = "https://www.example.com";\n        var modifiedHtml = await proxy.GetModifiedHtmlAsync(url);\n\n        if (modifiedHtml != null)\n        {\n            Console.WriteLine(modifiedHtml);\n        }\n        else\n        {\n            Console.WriteLine("Failed to retrieve or modify HTML.");\n        }\n    }\n}