using System.IO;\nusing HtmlAgilityPack;\n\n// Load CHM file\nusing (var chmFile = new ChmFile("example.chm"))\n{\n    // Extract HTML content\n    var html = chmFile.GetPageContent();\n\n    // Parse HTML content\n    var doc = new HtmlDocument();\n    doc.LoadHtml(html);\n\n    // Search for ISBN\n    var isbnNode = doc.DocumentNode.SelectSingleNode("//text()[contains(., 'ISBN')]");\n    if (isbnNode != null)\n    {\n        var isbn = isbnNode.InnerText.Trim();\n        Console.WriteLine($"ISBN: {isbn}");\n    }\n}