using EnvDTE;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Get the type of the DTE object\n        Type type = Type.GetTypeFromProgID("VisualStudio.DTE.15.0");\n\n        // Create a new instance of the DTE object\n        DTE dte = (DTE)Activator.CreateInstance(type);\n\n        // Make the new instance visible\n        dte.MainWindow.Visible = true;\n    }\n}