using System.IO;\nusing System.Reflection;\nusing System.Data.SQLite;\n\n// Get the embedded resource stream\nStream dbStream = Assembly.GetExecutingAssembly().GetManifestResourceStream("Title.Storage.storage.db3");\n\n// Create a temporary file to hold the database\nstring tempDbFile = Path.GetTempFileName();\n\n// Copy the embedded resource to the temporary file\nusing (FileStream fs = new FileStream(tempDbFile, FileMode.Create))\n{\n    dbStream.CopyTo(fs);\n}\n\n// Create a connection to the temporary database file\nSQLiteConnection con = new SQLiteConnection("Data Source=" + tempDbFile);\ncon.Open();\n\n// Use the connection as needed...\n\n// Don't forget to delete the temporary file when you're done!\nFile.Delete(tempDbFile);