string template = File.ReadAllText("EmailTemplate.html");\nstring subject = "The Subject";\nstring title = "My Styled Email";\nstring bodyContent = "This is the email content.";\n\nstring body = string.Format(template, subject, title, bodyContent);\n\n// Create a new MailMessage\nMailMessage message = new MailMessage();\nmessage.Subject = subject;\nmessage.Body = body;\nmessage.IsBodyHtml = true;\nmessage.BodyEncoding = System.Text.Encoding.UTF8;\n\n// Send the message using SmtpClient\nSmtpClient client = new SmtpClient("localhost");\nclient.Send(message);