using System;\nusing System.IO;\nusing System.Reflection;\n\npublic class DllChecker\n{\n    public static bool IsDebugBuild(string dllPath)\n    {\n        var assembly = Assembly.LoadFrom(dllPath);\n        var attributes = (AssemblyConfigurationAttribute[])assembly.GetCustomAttributes(typeof(AssemblyConfigurationAttribute), false);\n        return attributes.Length > 0 && attributes[0].Configuration == "Debug";\n    }\n}