[RoutePrefix("api/terms")]\npublic class TermsController : ApiController\n{\n    [HttpGet]\n    public JsonResult GetTerms()\n    {\n        var terms = _repository.GetTerms();\n        return Json(terms);\n    }\n\n    [HttpPost]\n    public IHttpActionResult CreateTerm(Term term)\n    {\n        _repository.CreateTerm(term);\n        return CreatedAtRoute("GetTerm", new { id = term.Id }, term);\n    }\n}