using System.Data;\nusing System.Globalization;\nusing System.Linq;\n\n// assume 'dataTable' is your DataTable with a DateTime column named 'DateColumn'\n\ndataTable.Columns["DateColumn"].DataType = typeof(string);\n\ndataTable.AsEnumerable()\n    .Select(row => row.Field<DateTime>("DateColumn")\n        .ToString(CultureInfo.CurrentCulture.DateTimeFormat.ShortDatePattern))\n    .ToList()\n    .ForEach(dateString => row.SetField("DateColumn", dateString));