using dnlib.DotNet;\nusing dnlib.Native;\n\n// Load the binary\nvar peImage = new PEImage("path/to/binary");\n\n// Get the x86 code\nvar code = peImage.ImageNtHeaders.OptionalHeader.Code;\n\n// Create a disassembler\nvar disassembler = new Disassembler(code, Architecture.X86);\n\n// Disassemble the code\nvar instructions = disassembler.Disassemble();\n\n// Iterate over the instructions\nforeach (var instruction in instructions)\n{\n    Console.WriteLine(instruction.ToString());\n}