using Microsoft.SharePoint;\n\n// Get the site collection\nSPSite site = new SPSite("http://yoursite");\n\n// Get the web\nSPWeb web = site.RootWeb;\n\n// Create a new permission level\nSPPermissionCollection permissions = web.RoleDefinitions;\nSPPermissionLevel permissionLevel = permissions.Add("Edit Own Items", "Edit own items");\n\n// Add permissions to edit own items\npermissionLevel.Permissions = SPBasePermissions.EditListItems | SPBasePermissions.AddListItems;\npermissionLevel.Permissions &= ~SPBasePermissions.DeleteListItems;\n\n// Add the permission level to the web\nweb.RoleDefinitions.Add(permissionLevel);\nweb.Update();