using System;\nusing System.Runtime.InteropServices;\nusing System.Diagnostics;\n\nclass Program\n{\n    [DllImport("user32.dll")]\n    static extern IntPtr SendMessage(IntPtr hWnd, uint Msg, IntPtr wParam, IntPtr lParam);\n\n    const uint WM_KEYDOWN = 0x0100;\n    const uint WM_KEYUP = 0x0101;\n\n    void SendF4ToProcess(Process process)\n    {\n        IntPtr hWnd = process.MainWindowHandle;\n        SendMessage(hWnd, WM_KEYDOWN, (IntPtr)0x73, IntPtr.Zero); // 0x73 is the code for F4\n        SendMessage(hWnd, WM_KEYUP, (IntPtr)0x73, IntPtr.Zero);\n    }\n\n    void ButtonClick(object sender, EventArgs e)\n    {\n        Process process = Process.Start("notepad.exe"); // replace with your process\n        SendF4ToProcess(process);\n    }\n}