using System.Web.HttpUtility;\n\n// Assume 'html' is the HTML markup generated by the WebHtmlEditor\nstring encodedHtml = HttpUtility.HtmlEncode(html);\n\n// Use a parameterized query to prevent SQL injection\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    SqlCommand command = new SqlCommand("INSERT INTO Pages (Html) VALUES (@Html)", connection);\n    command.Parameters.AddWithValue("@Html", encodedHtml);\n    command.ExecuteNonQuery();\n}