protected void Page_Load(object sender, EventArgs e)\n{\n    string nRef = Request.QueryString["n"];\n    string sRef = Request.QueryString["s"];\n\n    StringBuilder contentAttrBuilder = new StringBuilder("0;http://contact.cableone.net/main.aspx");\n    contentAttrBuilder.Append("?n=");\n    contentAttrBuilder.Append(HttpUtility.UrlEncode(nRef)); // encode query string value\n    contentAttrBuilder.Append("&s=");\n    contentAttrBuilder.Append(HttpUtility.UrlEncode(sRef)); // encode query string value\n\n    HtmlMeta metaRedirect = new HtmlMeta();\n    metaRedirect.HttpEquiv = "refresh";\n    metaRedirect.Content = contentAttrBuilder.ToString();\n\n    this.Header.Controls.Add(metaRedirect);\n}