// MasterPage.master\n<%@ Master Language="C#" %>\n<!DOCTYPE html>\n<html>\n<head runat="server">\n    <title></title>\n</head>\n<body>\n    <form id="form1" runat="server">\n        <asp:ScriptManager ID="ScriptManager1" runat="server" />\n        <asp:UpdatePanel ID="UpdatePanel1" runat="server">\n            <ContentTemplate>\n                <asp:ContentPlaceHolder ID="MainContent" runat="server" />\n            </ContentTemplate>\n        </asp:UpdatePanel>\n        <asp:Button ID="btnChangePage" runat="server" Text="Change Page" OnClick="btnChangePage_Click" />\n    </form>\n</body>\n</html>\n\n// MasterPage.master.cs\nprotected void btnChangePage_Click(object sender, EventArgs e)\n{\n    // Load the new content page\n    MainContent.Controls.Clear();\n    Control control = LoadControl("PageB.ascx");\n    MainContent.Controls.Add(control);\n}\n\n// PageB.ascx\n<%@ Control Language="C#" %>\n<h1>This is Page B</h1>