using System;\nusing System.Net;\nusing System.Security.Cryptography.X509Certificates;\nusing System.Net.Security;\nusing Microsoft.Reporting.WebForms;\n\npublic class SSRSProxy : IReportServerProxy\n{\n    private readonly string _reportServerUrl;\n    private readonly string _username;\n    private readonly string _password;\n\n    public SSRSProxy(string reportServerUrl, string username, string password)\n    {\n        _reportServerUrl = reportServerUrl;\n        _username = username;\n        _password = password;\n    }\n\n    public ReportViewer GetReportViewer(string reportPath)\n    {\n        var reportViewer = new ReportViewer();\n        reportViewer.ServerReport.ReportServerUrl = new Uri(_reportServerUrl);\n        reportViewer.ServerReport.ReportPath = reportPath;\n\n        var credential = new NetworkCredential(_username, _password);\n        reportViewer.ServerReport.ReportServerCredentials = credential;\n\n        return reportViewer;\n    }\n}